class BootstrapDeploymentRunner < DeploymentRunner
  protected
    def deployment_type
      :bootstrap
    end

    def build_ssh_session
      SshSession.new(*instances.map(&:connection_string)) do
        run "apt-get update"
        run "apt-get install -y git-core"
        run "      echo '|1|sv3Od6rY/siudwEpT+g2xn7YnsU=|8Rtg7HTzZS0UmR2NHzALVgQdn2A= ssh-rsa 
                    |1|bCSVbXPailzmrTScHcmJkv56zxI=|zJu/QV7pkexhJorVEzrNXnY9UBk= ssh-rsa ' >> ~/.ssh/known_hosts"
        run "if [ -d /var/chef ]; then
          cd /var/chef && git pull
        else
          git clone git@github.com:giraffesoft/conductor-cookbooks.git /var/chef
        fi"
        run "source /etc/environment && /var/chef/bootstrap/bootstrap.sh"
      end
    end
end

