class BootstrapLog < Log
  self.command = 
      "apt-get update &&
        apt-get install -y git-core && 
      echo '|1|sv3Od6rY/siudwEpT+g2xn7YnsU=|8Rtg7HTzZS0UmR2NHzALVgQdn2A= ssh-rsa 
            |1|bCSVbXPailzmrTScHcmJkv56zxI=|zJu/QV7pkexhJorVEzrNXnY9UBk= ssh-rsa ' >> ~/.ssh/known_hosts && 
          if [ -d /var/chef ]; then
            cd /var/chef/bootstrap && git pull
          else
            git clone git@github.com:giraffesoft/conductor-cookbooks.git /var/chef
          fi &&
             /var/chef/bootstrap/bootstrap.sh "

  protected
    def notify_instance_of_success
      instance.bootstrapped!
    end

    def notify_instance_of_start
      instance.bootstrapping!
    end

    def notify_instance_of_failure
    end
end

